mapper.update:
UPDATE  USER_INFO
SET
        PASSWORD_WRONG_COUNT = #{account.passwordWrongCount},
        MODIFIED_AT = #{modifiedAt}
WHERE
        USER_ID = #{userId.id}

UPDATE  USER_INFO
SET
        PASSWORD = #{account.password},
        MODIFIED_AT = #{modifiedAt}
WHERE
        USER_ID = #{userId.id}

UPDATE  USER_INFO
SET
        ROLES = #{roles},
        MODIFIED_AT = #{modifiedAt}
WHERE
        USER_ID = #{userId.id}

mapper.delete:
UPDATE  USER_INFO
SET
        WITHDRAWN_AT = #{withdrawnAt}
WHERE
        USER_ID = #{userId.id}
